import React, { Component } from "react";
import  { Redirect } from 'react-router-dom';
import axios from "axios";
import { FormGroup,
         FormControl,
         } from "react-bootstrap";


export default class Signup extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      username: "",
      password: "",
      confirmPassword: "",
      redirect: null,
      passMatch: true,
      badUsername: false,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  //Cam added this
  handleRegisterButtonClick = () => {

    //check if passwords match here before proceeding?
	var check = this.state.password === this.state.confirmPassword;
	if (check) {
		 var r = "/signup?name="+this.state.username+"&password="+this.state.password;
          axios.get(r).then(response => {
              console.log(response.data)
         });
	} else {
        this.setState({
            passMatch: false,
        });
    }
	//need a way to notify user if password and confirmPassword are not equal
  }

  render() {
      return (
        <div className="signuppage">
          <h1>Sign Up </h1>
          <br/>
          <FormGroup controlId = "username" size = "lg">
            <FormControl
                value={this.state.username}
                onChange={this.handleChange}
                placeholder="Enter username"
            />
          </FormGroup>

          <FormGroup controlId="password" size = "lg">
             <FormControl
                value={this.state.password}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
              />
          </FormGroup>

          <FormGroup controlId="confirmPassword" size = "lg">
            <FormControl
                value={this.state.confirmPassword}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
            />
          </FormGroup>

          { this.state.passMatch ? null : <h6>Passwords do not match</h6> }
          { this.state.badUsername ? <h6>Username already in use</h6> : null }

          <h5>Already a user? <a href="/login" > Log in</a></h5>

          <br/><br/>

          <button block size="lg" onClick = {this.handleRegisterButtonClick} > 
              Register
          </button>
        </div>
      );
    }
  }
