import React, { Component } from "react";
import  { Redirect } from 'react-router-dom';
import axios from "axios";
import { FormGroup,
         FormControl,
         } from "react-bootstrap";



export default class Signup extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      username: "",
      password: "",
      confirmPassword: "",
      redirect: null,
      passMatch: true,
      badUsername: false,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  //Cam added this
  handleRegisterButtonClick = () => {
    const url = "/HomeworkHub/backend/login.php";
    let formData = new FormData();
    let data = '{"username":"' + this.state.username + '", "password":"' + this.state.password + '"}';
    formData.append("formData", data);
    axios.post(url, formData)
    .then(response => {
        var res = response["data"];
        //Need to add else if ("parent") when implementing parent side
        if (res == "teacher") {
          console.log("teacher user found");
          this.setState({
            redirect: "/admin/dashboard",
          });
        } else {
          this.setState({
            wrongPass: true,
          });
          console.log("user not found")
        }
    })
    .catch(err=>console.log(err));
  }

  render() {
      return (
        <div className="signuppage">
          <h1>Sign Up </h1>
          <br/>

          <FormGroup controlId = "username" size = "lg">
            <FormControl
                value={this.state.username}
                onChange={this.handleChange}
                placeholder="Enter username"
            />
          </FormGroup>

          <FormGroup controlId="password" size = "lg">
             <FormControl
                value={this.state.password}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
              />
          </FormGroup>

          <FormGroup controlId="confirmPassword" size = "lg">
            <FormControl
                value={this.state.confirmPassword}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
            />
          </FormGroup>
          <input type="radio" /> Teacher
          <input type="radio" /> Parent

          { this.state.passMatch ? null : <h6>Passwords do not match</h6> }
          { this.state.badUsername ? <h6>Username already in use</h6> : null }

          <h5>Already a user? <a href="/login" > Log in</a></h5>

          <br/><br/>

          <button block size="lg" onClick = {this.handleRegisterButtonClick} > 
              Register
          </button>
        </div>
      );
    }
  }
