import React, { Component } from "react";
import  { Redirect } from 'react-router-dom';
import axios from "axios";
import { FormGroup,
         FormControl,
         } from "react-bootstrap";



export default class Signup extends Component {
  constructor() {
    super();
    this.state = {
      isLoading: false,
      username: "",
      password: "",
      confirmPassword: "",
      type: null,
      redirect: null,
      passMatch: true,
      badUsername: false,
      selected: true,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }


  handleRegisterButtonClick = () => {
    if (this.state.password !== this.state.confirmPassword) {
        this.setState({
            passMatch: false,
          });
        return;
    }
    var parent = document.getElementById("Parent");
    var teacher = document.getElementById("Teacher");
    if (!teacher.checked && !parent.checked) {
        console.log("yay");
        this.setState({
            selected: false,
          });
        return;
    } else {
        this.setState({
            selected: true,
            type: teacher.checked ? teacher.value : parent.value,
        });
    }

    const url = "/HomeworkHub/backend/register.php";
    let formData = new FormData();
    let data = '{"username":"' + this.state.username + '", "password":"' + this.state.password + '", "type":"' + this.state.type + '"}';
    formData.append("formData", data);
    axios.post(url, formData)
    .then(response => {
        //if fails username is not valid
        var res = response["data"];
        if (res == 0) {
            console.log("success");
        } else if (res == 1) {
            console.log("there was an error in registration");
        } else {
            console.log("username already taken");
        }
    })
    .catch(err=>console.log(err));
  }

  render() {
      return (
        <div className="signuppage">
          <h1>Sign Up </h1>
          <br/>

          <FormGroup controlId = "username" size = "lg">
            <FormControl
                value={this.state.username}
                onChange={this.handleChange}
                placeholder="Enter username"
            />
          </FormGroup>

          <FormGroup controlId="password" size = "lg">
             <FormControl
                value={this.state.password}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
              />
          </FormGroup>

          <FormGroup controlId="confirmPassword" size = "lg">
            <FormControl
                value={this.state.confirmPassword}
                onChange={this.handleChange}
                type="password"
                placeholder="Enter Password"
            />
          </FormGroup>
          { this.state.passMatch ? null : <h6>Passwords do not match</h6> }
          { this.state.badUsername ? <h6>Username already in use</h6> : null }
          <br/>
          <input type="radio" id = "Teacher" name = "type" value = "teacher" /> Teacher 
          <br/>   
          <input type="radio" id = "Parent" name = "type" value = "parent" /> Parent
          { this.state.selected ? <br/> : <h6>Please select account type</h6> }

          <h5>Already a user? <a href="/login" > Log in</a></h5>

          <br/>

          <button block size="lg" onClick = {this.handleRegisterButtonClick} > 
              Register
          </button>
        </div>
      );
    }
  }
